package lacosRepeticao.reticaoDoWhile;

public class ExemploDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		//(faca{...}enquanto();
		
		int contador =10;//variavel de controle
		
		do {
			contador ++;
			System.out.println("aqui estamos executando esse do while");
			System.out.println("O valor do contador Ã©: " + contador );
			
		}while(contador < 3);
	}

}
